import { PopupActionTypes } from './popup.actions';
export const initialState = {
  popupProjectMenu: {
    isOpen: false,
    position: 15
  },
  popupProjectForm: {
    isOpen: false,
    top: 0,
    left: 0
  }
};
export function reducer(state = initialState, action) {
  switch (action.type) {
    case PopupActionTypes.OpenProjectMenu:
      return Object.assign({}, state, {
        popupProjectMenu: {
          isOpen: true,
          position: action.position
        },
        popupProjectForm: {
          isOpen: false,
          top: 0,
          left: 0
        }
      });
    case PopupActionTypes.CloseProjectMenu:
      return Object.assign({}, state, {
        popupProjectMenu: {
          isOpen: false,
          position: 15
        },
        popupProjectForm: {
          isOpen: false,
          top: 0,
          left: 0
        }
      });
    case PopupActionTypes.OpenProjectFormMenu:
      {
        return Object.assign({}, state, {
          popupProjectForm: {
            isOpen: true,
            top: action.top,
            left: action.left
          }
        });
      }
    case PopupActionTypes.CloseProjectFormMenu:
      {
        return Object.assign({}, state, {
          popupProjectForm: {
            isOpen: false,
            top: 0,
            left: 0
          }
        });
      }
    default:
      return state;
  }
}
export const getPopupProjectMenu = state => state.popupProjectMenu;
export const getPopupProjectForm = state => state.popupProjectForm;
